import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { User } from '../Interface/interface';

// ✅ Interfaces
interface Course {
  name: string;
  description: string;
  price: number;
  image?: string;
}

interface User {
  id: number;
  name: string;
  email: string;
  password?: string;
  enrolledCourses: Course[];
}


@Component({
  selector: 'app-explore-courses',
  templateUrl: './explore-courses.component.html',
  styleUrls: ['./explore-courses.component.css']
})
export class ExploreCoursesComponent implements OnInit {




  courses: any[] = [];
  selectedCourse: any = null;
  showModal: boolean = false;

  student = {
    name: '',
    email: ''
  };

  apiUrl = 'http://localhost:3000/users';

  constructor(private router: Router, private http: HttpClient) { }

  ngOnInit(): void {
    const storedCourses = localStorage.getItem('courses');
    this.courses = storedCourses ? JSON.parse(storedCourses) : [];
  }

  enroll(course: any): void {
    const loggedInUser = JSON.parse(localStorage.getItem('loggedInUser') || '{}');
    if (!loggedInUser.email) {
      alert('⚠️ Please login to enroll.');
      this.router.navigate(['/login']);
      return;
    }

    this.selectedCourse = course;
    this.student = { name: '', email: '' }; // Ask again for name and email
    this.showModal = true;
  }

  confirmEnrollment(): void {
    const { name, email } = this.student;

    if (!name || !email) {
      alert('❗ Please enter your name and email.');
      return;
    }

    this.http.get<User[]>(this.apiUrl).subscribe(users => {
      const user = users.find(u => u.email === email && u.name === name);
      if (!user) {
        alert('❌ User not found. Please check your name and email.');
        return;
      }

      if (!user.enrolledCourses) {
        user.enrolledCourses = [];
      }

      const alreadyEnrolled = user.enrolledCourses.some(
        (c: any) => c.name === this.selectedCourse.name
      );

      if (alreadyEnrolled) {
        alert('⚠️ Already enrolled.');
        return;
      }

      // 2. Push and update via PUT
      user.enrolledCourses.push(this.selectedCourse);

      this.http.put(`${this.apiUrl}/${user.id}`, user).subscribe(() => {
        alert(`✅ Enrolled in "${this.selectedCourse.name}"`);
        this.closeModal();
      }, err => {
        alert('❌ Error saving enrollment');
        console.error(err);
      });
    });
  }

  closeModal(): void {
    this.showModal = false;
    this.selectedCourse = null;
    this.student = { name: '', email: '' };
  }
}
