import { Component } from '@angular/core';
import { Course } from 'src/app/Components/Interface/interface';

@Component({
  selector: 'app-manage-courses',
  templateUrl: './manage-courses.component.html',
  styleUrls: ['./manage-courses.component.css']
})
export class ManageCoursesComponent {

  courses: Course[] = [
    { title: 'Web Development', description: 'Learn HTML, CSS, JavaScript, and Angular.' },
    { title: 'Java Programming', description: 'Core and Advanced Java topics.' }
  ];

  newCourse: Course = { title: '', description: '' };

  addCourse() {
    if (this.newCourse.title && this.newCourse.description) {
      this.courses.push({ ...this.newCourse });
      this.newCourse = { title: '', description: '' };
      // Close modal manually
      const closeModal = document.getElementById('closeModalBtn');
      if (closeModal) closeModal.click();
    }
  }

  deleteCourse(index: number) {
    this.courses.splice(index, 1);
  }
}
