import { HttpClient } from '@angular/common/http';
import { Component } from '@angular/core';

@Component({
  selector: 'app-mycourse',
  templateUrl: './mycourse.component.html',
  styleUrls: ['./mycourse.component.css']
})
export class MycourseComponent {
  myCourses: any[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    const user = JSON.parse(localStorage.getItem('loggedInUser') || '{}');
    const allEnrollments = JSON.parse(localStorage.getItem('enrolledCourses') || '[]');

    if (user && user.email) {
      this.myCourses = allEnrollments.filter((course: any) => course.userEmail === user.email);
    } else {
      alert("Please log in to view your courses.");
    }
  }

  deleteCourse(courseId: number, index: number): void {
    if (confirm('Are you sure you want to delete this course?')) {
      this.http.delete(`http://localhost:8080/api/courses/${courseId}`).subscribe({
        next: () => {
          this.myCourses.splice(index, 1); // Remove from local list
        },
        error: (err) => {
          console.error('Delete failed', err);
          alert('Failed to delete course.');
        }
      });
    }
  }

}
