import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { FormServiceService } from 'src/app/service/form-service.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  loginForm: FormGroup;
  submitted = false;
  loginError = '';

  constructor(private fb: FormBuilder, private router: Router, private userserve: FormServiceService) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required],
    });
  }
  //   onSubmit() {
  //     const { email, password } = this.loginForm.value;

  //     // ðŸ” Check static admin login
  //     if (this.userserve.isAdmin(email, password)) {
  //       alert('Welcome Admin!');
  //       this.router.navigate(['/admin']);
  //       return;
  //     }

  //     // ðŸ‘¤ Check dynamic users from JSON
  //     this.userserve.loginUser(email, password).subscribe(users => {
  //       if (users.length > 0) {
  //         alert('Welcome User!');
  //         this.router.navigate(['/user']);
  //       } else {
  //         alert('Invalid credentials');
  //       }
  //     });
  //   }
  // }
  onSubmit() {
    const { email, password } = this.loginForm.value;
    const loginUser = {
      name: user.name,
      email: user.email,
      loginTime: new Date()
    };

    localStorage.setItem('loggedInUser', JSON.stringify(loginUser));

    const history = JSON.parse(localStorage.getItem('loggedUsers') || '[]');
    history.push(loginUser);
    localStorage.setItem('loggedUsers', JSON.stringify(history));


    if (this.userserve.isAdmin(email, password)) {
      localStorage.setItem('isLoggedIn', 'true');
      localStorage.setItem('role', 'admin');
      this.router.navigate(['/admin']);
      return;
    }

    this.userserve.loginUser(email, password).subscribe(users => {
      if (users.length > 0) {
        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('role', 'user');
        this.router.navigate(['/user']);
      } else {
        alert('Invalid credentials');
      }
    });
  }
}
