import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  showForm = false;
  selectedCourse: any = null;

  courses = [
    {
      id: 1,
      name: 'Angular Development',
      description: 'Build modern web apps with Angular.',
      price: 4999
    },
    {
      id: 2,
      name: 'Python for Data Science',
      description: 'Learn Python and data analysis tools.',
      price: 3999
    },
    {
      id: 3,
      name: 'Java Full Stack',
      description: 'Backend + Frontend with Java.',
      price: 5999
    }
  ];

  enrollData = {
    name: '',
    email: ''
  };

  handleEnrollClick(course: any) {
    this.selectedCourse = course;
    this.showForm = true;
  }

  submitForm() {
    const enrollment = {
      ...this.selectedCourse,
      name: this.enrollData.name,
      email: this.enrollData.email,
      status: 'Active',
      progress: 0
    };

    const enrolled = JSON.parse(localStorage.getItem('enrolledCourses') || '[]');
    enrolled.push(enrollment);
    localStorage.setItem('enrolledCourses', JSON.stringify(enrolled));

    alert(`🎉 Enrolled in ${this.selectedCourse.name} successfully!`);
    this.showForm = false;
    this.enrollData = { name: '', email: '' };
    this.selectedCourse = null;
  }
}
