import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-manage-courses',
  templateUrl: './manage-courses.component.html',
  styleUrls: ['./manage-courses.component.css']
})
export class ManageCoursesComponent implements OnInit {
  imageUrlInput: string = '';
  courses: any[] = [];
  editingIndex: number | null = null;
  editedCourse: any = {};
  newCourse = {
    name: '',
    description: '',
    price: null,
    image: ''
  };

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.loadCourses();
  }

  loadCourses() {
    this.http.get<any[]>('http://localhost:3000/courses').subscribe(data => {
      this.courses = data;
    });
  }

  setImageFromUrl() {
    this.newCourse.image = this.imageUrlInput;
  }

  onImageSelected(event: any) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = () => {
        this.newCourse.image = reader.result as string;
        this.imageUrlInput = '';
      };
      reader.readAsDataURL(file);
    }
  }

  addCourse() {
    if (this.newCourse.name && this.newCourse.description && this.newCourse.price && this.newCourse.image) {
      this.http.post('http://localhost:3000/courses', this.newCourse).subscribe(() => {
        alert('✅ Course added!');
        this.newCourse = { name: '', description: '', price: null, image: '' };
        this.loadCourses(); // reload updated list
      });
    }
  }

  deleteCourse(courseId: number) {
    if (confirm('Are you sure you want to delete this course?')) {
      this.http.delete(`http://localhost:3000/courses/${courseId}`).subscribe(() => {
        alert('🗑️ Course deleted!');
        this.loadCourses();
      });
    }
  }

  startEdit(course: any) {
    this.editingIndex = course.id;
    this.editedCourse = { ...course };
  }

  cancelEdit() {
    this.editingIndex = null;
    this.editedCourse = {};
  }

  saveEdit() {
    if (this.editingIndex !== null) {
      this.http.put(`http://localhost:3000/courses/${this.editingIndex}`, this.editedCourse).subscribe(() => {
        alert('✅ Course updated!');
        this.cancelEdit();
        this.loadCourses();
      });
    }
  }
}
