import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  availableCourses = [
    { id: 1, name: 'Angular Basics', description: 'Learn the fundamentals of Angular.' },
    { id: 2, name: 'Data Structures', description: 'Understand how data is organized.' },
    { id: 3, name: 'Web Development', description: 'HTML, CSS, JS for modern web.' }
  ];

  constructor(private router: Router) { }

  enroll(course: any) {
    const isLoggedIn = localStorage.getItem('isLoggedIn');
    const role = localStorage.getItem('role');

    if (isLoggedIn !== 'true' || role !== 'user') {
      alert('⚠️ Please login to enroll.');
      this.router.navigate(['/login'], { queryParams: { returnUrl: '/enroll' } });
      return;
    }

    // Save enrolled course in localStorage
    const enrolledCourses = JSON.parse(localStorage.getItem('enrolledCourses') || '[]');
    const alreadyEnrolled = enrolledCourses.find((c: any) => c.id === course.id);

    if (alreadyEnrolled) {
      alert('✅ You are already enrolled in this course.');
    } else {
      enrolledCourses.push({ ...course, status: 'Active', progress: 0 });
      localStorage.setItem('enrolledCourses', JSON.stringify(enrolledCourses));
      alert(`🎉 Enrolled in ${course.name} successfully!`);
    }
  }
}
