import { Component, OnInit } from '@angular/core';

interface Course {
  id: number;
  name: string;
  description: string;
  price: number;
  image: string;
}

@Component({
  selector: 'app-explore-courses',
  templateUrl: './explore-courses.component.html'
})
export class ExploreCoursesComponent implements OnInit {
  courses: Course[] = [];

  ngOnInit(): void {
    const courseData = localStorage.getItem('courses');
    if (courseData) {
      this.courses = JSON.parse(courseData);
    }
  }
}
