import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { FormServiceService } from 'src/app/service/form-service.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {

  loginForm: FormGroup;

  constructor(
    private fb: FormBuilder,
    private router: Router,
    private userService: FormServiceService
  ) {
    this.loginForm = this.fb.group({
      email: ['', [Validators.required, Validators.email]],
      password: ['', Validators.required]
    });
  }

  onSubmit() {
    const { email, password } = this.loginForm.value;

    // ✅ 1. Check for admin
    if (this.userService.isAdmin(email, password)) {
      localStorage.setItem('isLoggedIn', 'true');
      localStorage.setItem('role', 'admin');
      this.router.navigate(['/admin']);
      return;
    }

    // ✅ 2. Dynamic User Login from API
    this.userService.loginUser(email, password).subscribe(users => {
      if (users.length > 0) {
        const user = users[0];

        const loginUser = {
          name: user.name,
          email: user.email,
          role: user.role || 'user',
          loginTime: new Date()
        };

        localStorage.setItem('isLoggedIn', 'true');
        localStorage.setItem('role', loginUser.role);
        localStorage.setItem('loggedInUser', JSON.stringify(loginUser));

        alert(`Welcome, ${loginUser.name}!`);
        this.router.navigate(['/user']); // ✅ Success
      } else {
        alert('Invalid email or password.');
      }
    });
  }
}
