import { ViewportScroller } from '@angular/common';
import { Component } from '@angular/core';


@Component({
  selector: 'app-manage-courses',
  templateUrl: './manage-courses.component.html',
  styleUrls: ['./manage-courses.component.css']
})
export class ManageCoursesComponent {


  courseName: string = '';
  courseDescription: string = '';
  allCourses: any[] = [];

  constructor() {
    const storedCourses = localStorage.getItem('courses');
    if (storedCourses) {
      this.allCourses = JSON.parse(storedCourses);
    }
  }

  addCourse() {
    if (!this.courseName || !this.courseDescription) {
      alert('Please enter both course name and description.');
      return;
    }

    const newCourse = {
      id: Date.now(),
      name: this.courseName,
      description: this.courseDescription,
    };

    this.allCourses.push(newCourse);
    localStorage.setItem('courses', JSON.stringify(this.allCourses));

    this.courseName = '';
    this.courseDescription = '';
    alert('Course added successfully!');
  }
}
