import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { User } from '../Interface/interface';

interface Course {
  name: string;
  description: string;
  price: number;
  image?: string;
}

@Component({
  selector: 'app-explore-courses',
  templateUrl: './explore-courses.component.html',
  styleUrls: ['./explore-courses.component.css']
})
export class ExploreCoursesComponent implements OnInit {

  courses: Course[] = [];
  selectedCourse: Course | null = null;
  showModal: boolean = false;

  student = {
    name: '',
    email: ''
  };

  apiUrl = 'http://localhost:3000/users';

  constructor(private http: HttpClient, private router: Router) { }

  ngOnInit(): void {
    const storedCourses = localStorage.getItem('courses');
    this.courses = storedCourses ? JSON.parse(storedCourses) : [];
  }

  enroll(course: Course): void {
    const loggedIn = JSON.parse(localStorage.getItem('loggedInUser') || '{}');
    if (!loggedIn || !loggedIn.email) {
      alert('⚠️ Please login to enroll.');
      this.router.navigate(['/login']);
      return;
    }

    this.selectedCourse = course;
    this.student = { name: '', email: '' };
    this.showModal = true;
  }

  confirmEnrollment(): void {
    console.log('Confirm clicked!', this.student);
    const { name, email } = this.student;

    if (!name || !email) {
      alert('❗ Please enter your name and email.');
      return;
    }

    this.http.get<User[]>(this.apiUrl).subscribe((users: User[]) => {
      const user = users.find(u => u.email === email && u.name === name);

      if (!user) {
        alert('❌ User not found. Check your name and email.');
        return;
      }

      if (!user.enrolledCourses) {
        user.enrolledCourses = [];
      }

      const alreadyEnrolled = user.enrolledCourses.some(c => c.name === this.selectedCourse?.name);
      if (alreadyEnrolled) {
        alert('⚠️ Already enrolled in this course.');
        return;
      }

      user.enrolledCourses.push(this.selectedCourse!);

      this.http.put(`${this.apiUrl}/${user.id}`, user).subscribe(() => {
        alert(`✅ Enrolled in "${this.selectedCourse?.name}"!`);
        this.closeModal();
      }, err => {
        console.error(err);
        alert('❌ Failed to enroll. Try again.');
      });
    });
  }

  closeModal(): void {
    this.showModal = false;
    this.selectedCourse = null;
    this.student = { name: '', email: '' };
  }
}
