import { Component } from '@angular/core';

@Component({
  selector: 'app-reports',
  templateUrl: './reports.component.html',
  styleUrls: ['./reports.component.css']
})
export class ReportsComponent {

  // Original full list of reports
  allReports = [
    { type: 'Sales', date: new Date('2025-07-01'), details: 'Order #1234', amount: 12000 },
    { type: 'Users', date: new Date('2025-07-05'), details: 'New signup: Sam', amount: 0 },
    { type: 'Traffic', date: new Date('2025-07-07'), details: 'From Google Ads', amount: 0 },
    { type: 'Sales', date: new Date('2025-07-10'), details: 'Order #1235', amount: 8500 },
    { type: 'Users', date: new Date('2025-07-15'), details: 'New signup: Alex', amount: 0 }
  ];

  // Displayed filtered list
  reports = [...this.allReports];

  // Filter values bound to ngModel
  selectedType: string = '';
  startDate: string = '';
  endDate: string = '';

  // Summary cards data
  reportSummary = {
    sales: 0,
    users: 0,
    visits: 1240 // Static value for now
  };

  constructor() {
    this.updateSummary();
  }

  // ðŸ” Apply filter when button clicked
  filterReports() {
    this.reports = this.allReports.filter(report => {
      const matchesType = this.selectedType ? report.type === this.selectedType : true;

      const matchesStart = this.startDate ? new Date(report.date) >= new Date(this.startDate) : true;
      const matchesEnd = this.endDate ? new Date(report.date) <= new Date(this.endDate) : true;

      return matchesType && matchesStart && matchesEnd;
    });

    this.updateSummary();
  }

  // ðŸ”„ Update summary cards after filtering
  updateSummary() {
    this.reportSummary.sales = this.reports
      .filter(r => r.type === 'Sales')
      .reduce((total, r) => total + r.amount, 0);

    this.reportSummary.users = this.reports
      .filter(r => r.type === 'Users').length;

    // visits remains static or can be adjusted if needed
  }
}
