import { ViewportScroller } from '@angular/common';
import { Component } from '@angular/core';


@Component({
  selector: 'app-manage-courses',
  templateUrl: './manage-courses.component.html',
  styleUrls: ['./manage-courses.component.css']
})
export class ManageCoursesComponent {





  showAddForm: boolean = false;
  searchText: string = '';

  courses: Course[] = [
    {
      id: 1,
      title: 'Angular for Beginners',
      instructor: 'John Doe',
      category: 'Web Development',
      price: 2999,

    },
    {
      id: 2,
      title: 'Python Programming',
      instructor: 'Jane Smith',
      category: 'Programming',
      price: 1999,

    }
  ];

  newCourse: Course = this.getEmptyCourse();

  toggleAddForm(): void {
    this.showAddForm = !this.showAddForm;
    if (!this.showAddForm) this.resetForm();
  }

  addCourse(): void {
    const courseToAdd: Course = { ...this.newCourse, id: Date.now() };
    this.courses.push(courseToAdd);
    this.resetForm();
    this.showAddForm = false;
  }


  resetForm(): void {
    this.newCourse = this.getEmptyCourse();
  }

  filteredCourses(): Course[] {
    if (!this.searchText.trim()) return this.courses;
    const search = this.searchText.toLowerCase();
    return this.courses.filter(course =>
      course.title.toLowerCase().includes(search) ||
      course.instructor.toLowerCase().includes(search) ||
      course.category.toLowerCase().includes(search)
    );
  }

  private getEmptyCourse(): Course {
    return {
      id: 0,
      title: '',
      instructor: '',
      category: '',
      price: 0,

    };
  }


}

interface Course {
  id: number;
  title: string;
  instructor: string;
  category: string;
  price: number;

}
