import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  course = {
    id: 1,
    name: 'Angular Basics',
    description: 'Learn Angular with hands-on practice.'
  };

  showForm = false;

  enrollData = {
    name: '',
    email: ''
  };

  constructor(private router: Router) { }

  handleEnrollClick() {
    const isLoggedIn = localStorage.getItem('isLoggedIn');
    const role = localStorage.getItem('role');

    if (isLoggedIn !== 'true' || role !== 'user') {
      alert('🔒 Please log in first to enroll.');
      this.router.navigate(['/login'], { queryParams: { returnUrl: '/' } }); // Go back to home
      return;
    }

    // If already logged in, show form
    this.showForm = true;
  }

  submitEnrollment() {
    const enrolledCourses = JSON.parse(localStorage.getItem('enrolledCourses') || '[]');

    // Check if already enrolled
    const already = enrolledCourses.find((c: any) => c.id === this.course.id);
    if (already) {
      alert('✅ You are already enrolled.');
      return;
    }

    enrolledCourses.push({
      ...this.course,
      name: this.enrollData.name,
      email: this.enrollData.email,
      status: 'Active',
      progress: 0
    });

    localStorage.setItem('enrolledCourses', JSON.stringify(enrolledCourses));
    alert('🎉 Enrollment successful!');
    this.showForm = false;
    this.enrollData = { name: '', email: '' };
  }
}
