import { ViewportScroller } from '@angular/common';
import { Component } from '@angular/core';


@Component({
  selector: 'app-manage-courses',
  templateUrl: './manage-courses.component.html',
  styleUrls: ['./manage-courses.component.css']
})
export class ManageCoursesComponent {


  imageUrlInput: string = '';
  courses: any[] = [];

  newCourse = {
    name: '',
    description: '',
    price: null,
    image: ''
  };

  ngOnInit() {
    // Load from localStorage if exists
    const savedCourses = localStorage.getItem('courses');
    if (savedCourses) {
      this.courses = JSON.parse(savedCourses);
    }
  }

  // ðŸ“¸ If admin pastes image URL
  setImageFromUrl() {
    this.newCourse.image = this.imageUrlInput;
  }

  // ðŸ“· If admin uploads from system
  onImageSelected(event: any) {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();

      reader.onload = () => {
        this.newCourse.image = reader.result as string;
        this.imageUrlInput = ''; // Clear manual input
      };

      reader.readAsDataURL(file);
    }
  }

  addCourse() {
    if (
      this.newCourse.name &&
      this.newCourse.description &&
      this.newCourse.price !== null &&
      this.newCourse.image
    ) {
      this.courses.push({ ...this.newCourse });
      localStorage.setItem('courses', JSON.stringify(this.courses));
      this.newCourse = { name: '', description: '', price: null, image: '' };
      alert('Course added successfully!');
    }
  }


  // Delete course by index
  deleteCourse(index: number) {
    if (confirm('Are you sure you want to delete this course?')) {
      this.courses.splice(index, 1);
      localStorage.setItem('courses', JSON.stringify(this.courses));
      alert('Course deleted successfully!');
    }
  }

  // Edit course: store selected course in localStorage and redirect
  editCourse(index: number) {
    const selectedCourse = this.courses[index];
    localStorage.setItem('editCourse', JSON.stringify({ ...selectedCourse, index }));
    // Navigate to course edit page (you need to define this route)
    window.location.href = '/admin/edit-course'; // or use Angular router
  }
}
