import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-my-courses',
  templateUrl: './my-courses.component.html',
  styleUrls: ['./my-courses.component.css']
})
export class MyCoursesComponent implements OnInit {
  myCourses: any[] = [];

  constructor(private http: HttpClient) { }

  ngOnInit(): void {
    const user = JSON.parse(localStorage.getItem('loggedInUser') || '{}');

    if (!user.email) {
      alert("Please log in first.");
      return;
    }

    // ✅ Fetch latest user from backend (not localStorage)
    this.http.get<any[]>(`http://localhost:3000/users?email=${user.email}`).subscribe(users => {
      if (users.length > 0) {
        this.myCourses = users[0].myCourses || [];
        console.log('Fetched myCourses from API:', this.myCourses);
      } else {
        alert("User not found.");
      }
    });
  }

  // ✅ Delete course from local list and localStorage
  deleteCourse(courseId: number): void {
    if (confirm('Are you sure you want to delete this course?')) {
      const user = JSON.parse(localStorage.getItem('loggedInUser') || '{}');
      let allEnrollments = JSON.parse(localStorage.getItem('enrolledCourses') || '[]');

      // ❌ Remove the course for this user only
      allEnrollments = allEnrollments.filter((course: any) =>
        !(course.courseId === courseId && course.userEmail === user.email)
      );

      // ✅ Update localStorage
      localStorage.setItem('enrolledCourses', JSON.stringify(allEnrollments));

      // ✅ Update myCourses list for UI
      this.myCourses = this.myCourses.filter(course => course.courseId !== courseId);
    }
  }
}
