import { ViewportScroller } from '@angular/common';
import { Component } from '@angular/core';
import { Course } from 'src/app/Components/Interface/interface';

@Component({
  selector: 'app-manage-courses',
  templateUrl: './manage-courses.component.html',
  styleUrls: ['./manage-courses.component.css']
})
export class ManageCoursesComponent {


  searchText = '';
  selectedCourse: any = null;

  courses = [
    {
      id: 1,
      title: 'Angular Full Course',
      instructor: 'John Doe',
      category: 'Web Development',
      price: 2999,
      thumbnail: 'https://via.placeholder.com/300x200.png?text=Angular+Course'
    },
    {
      id: 2,
      title: 'Python for Beginners',
      instructor: 'Jane Smith',
      category: 'Programming',
      price: 1999,
      thumbnail: 'https://via.placeholder.com/300x200.png?text=Python+Course'
    },
    {
      id: 3,
      title: 'Machine Learning Mastery',
      instructor: 'Mike Johnson',
      category: 'AI & ML',
      price: 3999,
      thumbnail: 'https://via.placeholder.com/300x200.png?text=ML+Course'
    }
  ];

  filteredCourses() {
    if (!this.searchText.trim()) return this.courses;
    return this.courses.filter(course =>
      course.title.toLowerCase().includes(this.searchText.toLowerCase()) ||
      course.instructor.toLowerCase().includes(this.searchText.toLowerCase()) ||
      course.category.toLowerCase().includes(this.searchText.toLowerCase())
    );
  }

  editCourse(course: any) {
    this.selectedCourse = { ...course }; // clone for safe editing
  }

  cancelEdit() {
    this.selectedCourse = null;
  }

  saveCourse() {
    const index = this.courses.findIndex(c => c.id === this.selectedCourse.id);
    if (index > -1) {
      this.courses[index] = { ...this.selectedCourse };
      this.selectedCourse = null;
    }
  }

  deleteCourse(id: number) {
    this.courses = this.courses.filter(course => course.id !== id);
    if (this.selectedCourse?.id === id) {
      this.selectedCourse = null;
    }
  }
}
