import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-explore-courses',
  templateUrl: './explore-courses.component.html',
  styleUrls: ['./explore-courses.component.css']
})
export class ExploreCoursesComponent implements OnInit {

  courses: any[] = [];
  selectedCourse: any = null;
  showModal = false;

  constructor(private router: Router) { }

  ngOnInit() {
    const storedCourses = localStorage.getItem('courses');
    this.courses = storedCourses ? JSON.parse(storedCourses) : [];


  }

  enroll(course: any) {
    const user = localStorage.getItem('loggedInUser');
    if (!user) {
      alert("‚ùóPlease login to enroll.");
      this.router.navigate(['/login']);
      return;
    }

    this.selectedCourse = course;
    this.showModal = true;
  }

  confirmEnrollment() {
    const user = JSON.parse(localStorage.getItem('loggedInUser')!);
    const key = `enrolled_${user.username}`;
    const enrolled = JSON.parse(localStorage.getItem(key) || '[]');

    // Prevent duplicate
    if (Array.isArray(enrolled) && enrolled.some(course => course?.name === this.selectedCourse?.name)) {
      alert('‚ö†Ô∏è Already enrolled in this course!');
    } else {
      enrolled.push(this.selectedCourse);
      localStorage.setItem(key, JSON.stringify(enrolled));
      alert(`‚úÖ Enrolled in "${this.selectedCourse.name}"!`);
    }


    this.closeModal();
  }

  closeModal() {
    this.showModal = false;
    this.selectedCourse = null;
  }
}
